static int calcuRotateCoordinates(int *outi, CFLOAT *out, CFLOAT *in, CFLOAT *angle) 
{
    CFLOAT r=0;
    CFLOAT x1, y1;
    CFLOAT x2, y2;
    CFLOAT cosA, sinA;

    if (!out) return -1;
    if (!in) return -2;
    if (!outi) return -3;
    if (!angle) return -4;

    //printf("calcu rotate input :%lf, %lf, angle:%lf \n", in[0], in[1], angle);

    x1 = in[0];
    y1 = in[1];

    cosA = angle[0];
    sinA = angle[1];

    //r = angle * M_PI / piAngle;
    //x2 = x1*cos(r) - y1*sin(r);
    //y2 = x1*sin(r) + y1*cos(r);
    
    x2 = x1*cosA - y1*sinA;
    y2 = x1*sinA + y1*cosA;
    
    out[0] = x2;
    out[1] = y2;

    outi[0] = (int)(x2);
    outi[1] = (int)(y2);

    //printf("calcu rotate input :%lf, %lf, output: (%d, %d) (%lf, %lf) \n", in[0], in[1], outi[0], outi[1], out[0], out[1]);
    
    return 0;
}

