static int findEOF(char *p, int max)
{
    int index[2] = {0, 0};
    char mark[2] = {0xff, 0xd9};
    char target = 0;
    int i, cnt=0;

    if (!p) return -1;
    if (max == 0) return -2;

    target = mark[0];
    for (i = 0; i < max; i++) {
        if (cnt > 0) {
            if (p[i] == target) {
                index[1] = i; 
                break;
            } else {
                cnt = 0;
                target = mark[0];
            }
        } else {
            if (p[i] == target) {
                index[0] = i; 
                cnt = 1;
                target = mark[1];
            }
        }    
    }

    if (i < max) {
        return index[0];
    } 

    return -3;
}

