static int pdfParamCalcu(int hi, int wh, int *mh, int *mw)
{
    CFLOAT jscale=0.0, tscale=0.0, dscale=0.0;;
    CFLOAT tw=0.0, th=0.0, sw=0.0, sh=0.0;;
    
    if (!mh) return -1;
    if (!mw) return -2;
    if (!hi) return -3;
    if (!wh) return -4;

    sh = hi;
    sw = wh;

    tscale = MAX_PDF_W / MAX_PDF_H;
    jscale = sw / sh;

    if (jscale > tscale) {
        tw = MAX_PDF_W;
        dscale = tw / sw;
        th = sh * dscale;
    } else {
        th = MAX_PDF_H;
        dscale = th / sh;
        tw = sw * dscale;
    }

    *mh = (int) th;
    *mw = (int) tw;

    return 0;
}

static int pdfAppend(char *d, char *s, int tot, int max)
{
    int idx = 0, slen = 0, end = 0;

    if (d == 0) return -1;
    if (s == 0) return -2;
    if (tot > max) return -3;

    slen = strlen(s);
    idx = tot;
    end = tot + slen;
    if (end > max) return -4;
    
    while (idx < end) {
        d[idx] = *s;
        idx ++;
        s++;
    }
    
    return slen;
}

