inline int aspCalcSupLen(struct supdataBack_s *sup)
{
    int len=0, cnt=0;
    struct supdataBack_s *scr;
    if (!sup) return -1;
    
    scr = sup;
    while(scr) {
        len += scr->supdataTot - scr->supdataUse;

        //printf("%d. calcu sup, tot / used / len = %d / %d / %d\n", cnt, scr->supdataTot, scr->supdataUse, len);
        scr = scr->n;

        cnt++;
    }
    return len;
}

static int aspPopSupOut(char *dst, struct supdataBack_s *str, int size, struct supdataBack_s **nxt)
{
    int ret = 0, acusz = 0;
    char *src1 = 0, *src2 = 0;
    int tot=0, usd=0, rst=0;
    struct supdataBack_s *scr=0, *snx=0;
    
    if (!str) return -1;
    if (!nxt) return -2;
    if (!dst) return -3;

    scr = str;
    snx = str->n;

    src1 = scr->supdataBuff;
    tot = scr->supdataTot;
    usd = scr->supdataUse;
    rst = tot - usd;

    if (rst < 0) return -4;
    if (rst == 0) return 0;

    if (size == rst) {
        memcpy(dst, src1+usd, size);
        acusz += size;
        *nxt = snx;
        scr->supdataUse += size;
    } else if (size > rst) {
        memcpy(dst, src1+usd, rst);
        acusz += rst;
        ret = aspPopSupOut(dst+rst, snx, size - rst, nxt);
        if (ret > 0) {
            acusz += ret;
        } else {
            *nxt = snx;
        }
        scr->supdataUse += rst;
    } else {
        memcpy(dst, src1+usd, size);    
        acusz += size;
        *nxt = scr;
        scr->supdataUse += size;
    }

    return acusz;
}

static uint32_t aspRawCompose(char * raw, int size)
{
    int sh[4] = {0, 8, 16, 24};
    int i = 0;
    uint32_t val = 0, tmp = 0;

    while(i < size) {
        tmp = raw[i];
        val |= tmp << sh[i];
        i++;
    }
    return val;
}

static uint32_t aspRawReverse(char * raw, int size, uint32_t val)
{
    int sh[4] = {0, 8, 16, 24};
    int i = 0;
    uint8_t tmp = 0;

    while(i < size) {
        tmp = (val >> sh[i]) & 0xff;
        raw[i] = tmp;
        i++;
        //printf("0x%.2x \n", tmp);
    }
    return 0;
}

