static int shmem_dump(char *src, int size)
{
    char str[128];
    int inc;
    if (!src) return -1;

    inc = 0;
    sprintf_f(str, "memdump[0x%.8x] sz%d: \n", src, size);
    print_f(mlogPool, "dump", str);
    while (inc < size) {
        sprintf_f(str, "%.2x ", *src);
        print_f(mlogPool, NULL, str);

        if (!((inc+1) % 16)) {
            sprintf_f(str, " %d \n", inc+1);
            print_f(mlogPool, NULL, str);
        }
        inc++;
        src++;
    }

    sprintf_f(str, "\n");
    print_f(mlogPool, NULL, str);

    return inc;
}
