        //perror("bind:");
        ret = -1;
        if (setsockopt(rs->psocket_n->listenfd, SOL_SOCKET, SO_REUSEADDR, &ret, sizeof(int)) == -1) {
            perror("setsockopt");    
            
            sprintf(rs->logs, "p7 get bind ret: %d", ret);
            error_handle(rs->logs, 24127);
        }
